// --- CONFIGURATION ---
// On d√©finit les actifs ET les protocoles √† surveiller
const symbolsToWatch = ['USDC', 'USDT', 'DAI', 'WETH', 'WBTC'];
const protocolsToWatch = ['aave-v3', 'compound-v3', 'spark'];
// --------------------

const allPools = $input.item.json.data;
const results = [];

// On boucle sur tous les pools re√ßus de l'API
for (const pool of allPools) {
  // On v√©rifie si le pool correspond √† nos crit√®res (cha√Æne, projet, symbole)
  if (
    pool.chain === 'Polygon' &&
    protocolsToWatch.includes(pool.project) &&
    symbolsToWatch.includes(pool.symbol) &&
    pool.tvlUsd > 1000 // On ignore les pools avec une tr√®s faible liquidit√©
  ) {

    const score = (pool.apy / Math.log(pool.tvlUsd)).toFixed(2);

    results.push({
      project: pool.project, // IMPORTANT : On ajoute le nom du projet
      symbol: pool.symbol,
      apy: (pool.apy * 100).toFixed(2) + '%',
      tvl: `$${(pool.tvlUsd / 1000000).toFixed(1)}M`,
      score: score
    });
  }
}

// On trie les r√©sultats par score, du plus √©lev√© au plus bas
results.sort((a, b) => b.score - a.score);

// On pr√©pare la sortie
const item = {};
item.resultsArray = results; // Contient maintenant les donn√©es de tous les protocoles
item.timestamp = new Date().toISOString();

return item;

