// === CODE FINAL ET D√âFINITIF POUR "Comparer_Analyser1" ===
// ========================================================================
const allInputs = $input.all();

// --- EXTRACTION TR√àS ROBUSTE DES DONN√âES (bas√©e sur le contenu) ---

