const defiInputItem = allInputs.find(item => item.json && item.json.resultsArray !== undefined);
const currentDefiData = defiInputItem ? defiInputItem.json : {};

// 2. On trouve l'item contenant les donn√©es de prix (il a une cl√© 'priceData')
const priceInputItem = allInputs.find(item => item.json && item.json.priceData !== undefined);
const priceData = priceInputItem ? priceInputItem.json : {};

// 3. Tout le reste est consid√©r√© comme l'historique
const historicalData = allInputs
  .filter(item => item !== defiInputItem && item !== priceInputItem)
  .map(item => item.json);


console.log(`Donn√©es historiques trouv√©es: ${historicalData.length} entr√©es`);
console.log(`Donn√©es DeFi actuelles trouv√©es: ${currentDefiData.resultsArray?.length || 0} pools`);
console.log(`Donn√©es de prix trouv√©es: ${priceData.priceData?.length || 0} actifs`);


// --- VALIDATION DES DONN√âES ESSENTIELLES ---
// Cette partie est maintenant cruciale et devrait fonctionner correctement.
if (!currentDefiData.resultsArray || currentDefiData.resultsArray.length === 0) {
