// --- FONCTIONS UTILITAIRES ---
function getStandardDeviation(array) {
  if (array.length < 2) return 0;
  const n = array.length;
  const mean = array.reduce((a, b) => a + b) / n;
  const variance = array.reduce((a, b) => a + (b - mean) ** 2, 0) / (n - 1);
  return Math.sqrt(variance);
}

function calculateOpportunityScore(apy, tvl, volatility, momentum = 0) {
  const apyScore = parseFloat(apy.replace('%', '')) / 100;
  const liquidityScore = Math.log(parseFloat(tvl.replace(/[$M]/g, '')) * 1000000) / 25;
  const stabilityScore = Math.max(0, 1 - (volatility / 10));
  const momentumBonus = Math.abs(momentum) / 100;
  return ((apyScore * liquidityScore * stabilityScore) + momentumBonus).toFixed(4);
}

function detectArbitrageOpportunity(protocols, symbol) {
  if (protocols.length < 2) return null;
  const sorted = protocols.sort((a, b) => parseFloat(b.apy) - parseFloat(a.apy));
  const spread = parseFloat(sorted[0].apy) - parseFloat(sorted[1].apy);
  if (spread > 0.5) {
    return {
      symbol,
      highest: { project: sorted[0].project, apy: sorted[0].apy },
      lowest: { project: sorted[1].project, apy: sorted[1].apy },
      spread: spread.toFixed(2),
      potential_profit: (spread * 1000).toFixed(0)
    };
  }
  return null;
}

const symbolsToWatch = ['USDC', 'USDT', 'DAI', 'WETH', 'WBTC'];

// --- TRAITEMENT DE L'HISTORIQUE ---
const historicalDataBySymbol = {};
historicalData.forEach(item => {
  if (item.symbol) {
    if (!historicalDataBySymbol[item.symbol]) {
      historicalDataBySymbol[item.symbol] = [];
    }
    const cleanApy = parseFloat(String(item.apy).replace('%', ''));
    if (!isNaN(cleanApy)) {
      historicalDataBySymbol[item.symbol].push(cleanApy);
    }
  }
});

// --- ENRICHISSEMENT AVEC DONN√âES PRIX ---
const enrichedData = currentDefiData.resultsArray.map(defiItem => {
  const priceInfo = priceData.priceData?.find(p => p.symbol === defiItem.symbol);
  return {
    ...defiItem,
    current_price: priceInfo?.price_usd || 0,
    volume_24h: priceInfo?.volume_24h || 0,
    price_change_24h: priceInfo?.price_change_24h || 0,
    momentum_score: priceInfo?.momentum_score || 0
  };
});

// --- ANALYSE ET ENRICHISSEMENT ---
let bestOpportunity = { symbol: 'N/A', project: 'N/A', opportunity_score: -1 };
let highestChange = { symbol: 'N/A', project: 'N/A', delta: -Infinity };
let lowestChange = { symbol: 'N/A', project: 'N/A', delta: Infinity };

const analyzedData = enrichedData.map(item => {
  const currentApy = parseFloat(item.apy.replace('%', ''));
  const assetHistory = historicalDataBySymbol[item.symbol] || [];
  const volatility = getStandardDeviation(assetHistory);
  let trend = 'üÜï';
  let deltaValue = 0;
  if (assetHistory.length > 0) {
    const lastApy = assetHistory[assetHistory.length - 1];
    deltaValue = currentApy - lastApy;
    if (deltaValue > highestChange.delta) {
      highestChange = { ...item, delta: deltaValue };
    }
    if (deltaValue < lowestChange.delta) {
      lowestChange = { ...item, delta: deltaValue };
    }
    if (Math.abs(deltaValue) > 0.1) {
      trend = deltaValue > 0 ? '‚¨ÜÔ∏è' : '‚¨áÔ∏è';
    } else {
      trend = '‚û°Ô∏è';
    }
  }
  const opportunityScore = calculateOpportunityScore(item.apy, item.tvl, volatility, item.momentum_score);
  if (parseFloat(opportunityScore) > parseFloat(bestOpportunity.opportunity_score)) {
    bestOpportunity = { ...item, opportunity_score: opportunityScore };
  }
  return { ...item, apy_float: currentApy, volatility: volatility.toFixed(2), trend, delta: deltaValue.toFixed(2), opportunity_score: opportunityScore, risk_level: volatility > 2 ? 'HIGH' : volatility > 1 ? 'MEDIUM' : 'LOW' };
});

// --- D√âTECTION D'ARBITRAGE ---
const arbitrageOpportunities = [];
symbolsToWatch.forEach(symbol => {
  const protocols = analyzedData.filter(d => d.symbol === symbol);
  const arbitrage = detectArbitrageOpportunity(protocols, symbol);
  if (arbitrage) {
    arbitrageOpportunities.push( arbitrage );
  }
});

// --- MEILLEURS TAUX PAR ACTIF ---
const bestRatesByAsset = {};
symbolsToWatch.forEach(symbol => {
  const bestForSymbol = analyzedData
    .filter(item => item.symbol === symbol)
    .reduce((best, current) => parseFloat(current.opportunity_score) > parseFloat(best.opportunity_score) ? current : best, { opportunity_score: -1 });
  if (bestForSymbol.project) {
    bestRatesByAsset[symbol] = { apy: bestForSymbol.apy, project: bestForSymbol.project, opportunity_score: bestForSymbol.opportunity_score, risk_level: bestForSymbol.risk_level, trend: bestForSymbol.trend };
  }
});

// --- G√âN√âRATION DES RAPPORTS ---
const trendReport = analyzedData.map(item => `[${item.project}] ${item.symbol}: ${item.apy} ${item.trend} (Œî${item.delta}%) | Risk: ${item.risk_level} | Score: ${item.opportunity_score}`).join('\n');
const radarText = Object.entries(bestRatesByAsset).map(([symbol, data]) => `‚Ä¢ **${symbol}**: ${data.apy} sur **${data.project}** ${data.trend} (Score: ${data.opportunity_score}, Risk: ${data.risk_level})`).join('\n');
const arbitrageReport = arbitrageOpportunities.length > 0 ? arbitrageOpportunities.map(opp => `üéØ **${opp.symbol}**: ${opp.spread}% d'√©cart | ${opp.highest.project} (${opp.highest.apy}) ‚Üí ${opp.lowest.project} (${opp.lowest.apy}) | Profit potentiel: $${opp.potential_profit}/1000$`).join('\n') : 'Aucune opportunit√© d\'arbitrage d√©tect√©e (seuil: 0.5%)';

// --- INDICATEURS CL√âS ---
const topOpportunity = analyzedData.reduce((best, current) => parseFloat(current.opportunity_score) > parseFloat(best.opportunity_score) ? current : best, { opportunity_score: -1 });
const highestVolatility = analyzedData.reduce((max, current) => parseFloat(current.volatility) > parseFloat(max.volatility) ? current : max, { volatility: -1 });

// --- DONN√âES POUR SAUVEGARDE ---
const dataForSheet = analyzedData.map(item => ({ symbol: item.symbol, project: item.project, apy: item.apy, tvl: item.tvl, score: item.opportunity_score, volatility: item.volatility, trend: item.trend, timestamp: new Date().toISOString() }));

// --- RETOUR FINAL ---
return {
  json: {
    trendReport,
    radarText,
    arbitrageReport,
    topOpportunity,
    highestVolatility,
    bestArbitrageOpportunity: arbitrageOpportunities[0] || null,
    highestChange: { ...highestChange, delta: highestChange.delta > -Infinity ? `+${highestChange.delta.toFixed(2)}%` : 'N/A' },
    lowestChange: { ...lowestChange, delta: lowestChange.delta < Infinity ? `${lowestChange.delta.toFixed(2)}%` : 'N/A' },
    timestamp: new Date().toISOString(),
    marketSentiment: priceData.marketSentiment || 'neutral',
    totalMarketChange: priceData.totalMarketChange || '0.00',
    resultsForSheet: dataForSheet,
    stats: { totalOpportunities: analyzedData.length, arbitrageOpportunities: arbitrageOpportunities.length, highRiskAssets: analyzedData.filter(item => item.risk_level === 'HIGH').length, dataQuality: { historicalDataAvailable: historicalData.length > 0, priceDataAvailable: priceData.priceData?.length > 0, completeness: (analyzedData.length / symbolsToWatch.length * 100).toFixed(0) + '%' } }
  }
};

// Traitement des donn√©es CoinGecko pour votre workflow
const coingeckoData = $input.item.json;
const processedPrices = [];


// Mapping des ID CoinGecko vers vos symboles
const symbolMapping = {
  'ethereum': 'WETH',
  'bitcoin': 'WBTC',
  'usd-coin': 'USDC',
  'tether': 'USDT',
  'dai': 'DAI',
  'matic-network': 'MATIC' //
};
// ... (le reste du code est correct)

// Traitement des donn√©es
for (const [coinId, data] of Object.entries(coingeckoData)) {
  const symbol = symbolMapping[coinId];
  if (symbol) {
    processedPrices.push({
      symbol: symbol,
      price_usd: data.usd,
