      // Calcul d'un score de momentum bas√© sur volume et changement de prix
      momentum_score: ((data.usd_24h_change || 0) * Math.log(data.usd_24h_vol || 1) / 1000).toFixed(4)
    });
  }
}

// Ajout d'un score de sentiment global bas√© sur les variations
const totalChange = processedPrices.reduce((sum, item) => sum + (item.price_change_24h || 0), 0);
const marketSentiment = totalChange > 0 ? 'bullish' : totalChange < -5 ? 'bearish' : 'neutral';

return { 
  json: { 
    priceData: processedPrices,
    marketSentiment: marketSentiment,
    totalMarketChange: totalChange.toFixed(2),
