  } 
};

// ========================================================================
// === CODE V9 (FINAL & ADAPT√â √Ä L'ARCHITECTURE MERGE) ===
// ========================================================================
console.log('--- D√âBUT DU TRAITEMENT FINAL (V9) ---');

const allInputs = $input.all();

// --- Identification robuste des 3 sources de donn√©es ---
// 1. Le rapport pr√©par√© (contient originalReport, actualDestSymbol, etc.)
const preparedItem = allInputs.find(item => item.json.originalReport !== undefined);
// 2. La r√©ponse de 1inch (contient toAmount en succ√®s, ou error en √©chec)
const oneInchItem = allInputs.find(item => item.json.toAmount !== undefined || item.json.error !== undefined);
// 3. Les prix de CoinGecko
const priceItem = allInputs.find(item => item.json.priceData !== undefined);

// Validation critique
if (!preparedItem || !oneInchItem || !priceItem) {
  throw new Error("Donn√©e manquante. Le noeud Merge doit fournir 3 items: le rapport pr√©par√©, la r√©ponse 1inch, et les prix.");
}

// --- Extraction des donn√©es ---
// Le rapport final est la base de notre travail
const finalReport = preparedItem.json.originalReport;
const oneInchResponse = oneInchItem.json;
const priceData = priceItem.json;

// --- Cas 1: La simulation a √©chou√© (manque de liquidit√©, etc.) ---
if (oneInchResponse.error) {
  console.log("√âchec de la simulation 1inch. Raison:", oneInchResponse.error.description);
  finalReport.profitAnalysis = {
    isProfitable: false,
    netProfitUSD: '0.00',
    summary: `‚ùå Simulation √©chou√©e: ${oneInchResponse.error.description || 'Pas assez de liquidit√©.'}`
  };
  return { json: finalReport };
}

// --- Cas 2: La simulation a r√©ussi, on calcule le profit ---
const actualDestSymbol = preparedItem.json.actualDestSymbol;
const tokenDecimals = preparedItem.json.tokenDecimals;
const maticPrice = priceData.priceData.find(p => p.symbol === 'MATIC')?.price_usd || 0;
const destPrice = priceData.priceData.find(p => p.symbol === actualDestSymbol)?.price_usd || 0;

if (maticPrice > 0 && destPrice > 0 && tokenDecimals) {
    const gasCostInUSD = (oneInchResponse.gas * 30 / 1_000_000_000) * maticPrice;
    const amountInUSD = 1000;
    const destDecimalsValue = tokenDecimals[actualDestSymbol] || 18;
    const amountOut = parseFloat(oneInchResponse.toAmount) / Math.pow(10, destDecimalsValue);
    const amountOutInUSD = amountOut * destPrice;
    const netProfitUSD = amountOutInUSD - amountInUSD - gasCostInUSD;

    finalReport.profitAnalysis = {
        isProfitable: netProfitUSD > 0,
        netProfitUSD: netProfitUSD.toFixed(2),
        summary: netProfitUSD > 0 
          ? `‚úÖ Simulation: 1000$ en ${actualDestSymbol} | Profit net: $${netProfitUSD.toFixed(2)}`
          : `‚ùå Simulation: 1000$ en ${actualDestSymbol} | Perte nette: $${Math.abs(netProfitUSD).toFixed(2)}`
    };
} else {
    finalReport.profitAnalysis = {
        isProfitable: false,
        netProfitUSD: '0.00',
        summary: `‚ùå √âchec calcul: Prix manquant pour MATIC ou ${actualDestSymbol}.`
    };
}

console.log('--- TRAITEMENT FINAL (V9) TERMIN√â ---');
return { json: finalReport };

