// =======================================================================

console.log('--- D√âBUT DU TRAITEMENT FINAL (V2) ---');

const allInputs = $input.all();
console.log(`Nombre d'items re√ßus: ${allInputs.length}`);

const marketReportItem = allInputs.find(item => item.json.topOpportunity !== undefined);
const oneInchItem = allInputs.find(item => item.json.gas !== undefined);
const priceDataItem = allInputs.find(item => item.json.priceData !== undefined);

const marketReport = marketReportItem ? marketReportItem.json : {};
const oneInchData = oneInchItem ? oneInchItem.json : {};
const priceData = priceDataItem ? priceDataItem.json : {};

if (!marketReportItem) {
  console.error("ERREUR CRITIQUE: Le rapport de march√© principal est manquant.");
  return { json: { error: "Le rapport de march√© principal n'a pas √©t√© trouv√©." } };
}

const finalReport = { ...marketReport };

// --- NOUVELLE SECTION DE CALCUL DE PROFIT AM√âLIOR√âE ---
// Initialisation par d√©faut
finalReport.profitAnalysis = {
  isProfitable: false,
  netProfitUSD: '0.00',
  summary: '‚ùå Simulation de trade non effectu√©e.' // Message par d√©faut
};

const hasOneInchData = oneInchData && oneInchData.gas && oneInchData.toAmount;
const hasPriceData = priceData && Array.isArray(priceData.priceData) && priceData.priceData.length > 0;

// On v√©rifie d'abord que les donn√©es de base sont l√†
if (!hasOneInchData) {
    finalReport.profitAnalysis.summary = '‚ùå √âchec de la simulation : Aucune r√©ponse de l\'API 1inch.';
} else if (!hasPriceData) {
    finalReport.profitAnalysis.summary = '‚ùå √âchec de la simulation : Donn√©es de prix du march√© manquantes.';
} else {
    // Si les donn√©es sont l√†, on tente le calcul
    try {
        const maticPrice = priceData.priceData.find(p => p.symbol === 'MATIC')?.price_usd || 0;
        const wethPrice = priceData.priceData.find(p => p.symbol === 'WETH')?.price_usd || 0;

        if (maticPrice <= 0 || wethPrice <= 0) {
            let errorMsg = 'Prix manquant pour ';
            if (maticPrice <= 0) errorMsg += 'MATIC ';
            if (wethPrice <= 0) errorMsg += 'WETH';
            finalReport.profitAnalysis.summary = `‚ùå √âchec de la simulation : ${errorMsg}.`;
        } else {
            const gasPriceGwei = 30;
            const gasCostInUSD = (oneInchData.gas * gasPriceGwei / 1_000_000_000) * maticPrice;
            const amountInUSD = 1000;
            const amountOutInUSD = (parseFloat(oneInchData.toAmount) / Math.pow(10, 18)) * wethPrice;

            const grossProfitUSD = amountOutInUSD - amountInUSD;
            const netProfitUSD = grossProfitUSD - gasCostInUSD;

            finalReport.profitAnalysis = {
                isProfitable: netProfitUSD > 0,
                netProfitUSD: netProfitUSD.toFixed(2),
                summary: netProfitUSD > 0 
                  ? `‚úÖ Simulation rentable ! Profit net: $${netProfitUSD.toFixed(2)}`
                  : `‚ùå Simulation non rentable. Perte nette: $${Math.abs(netProfitUSD).toFixed(2)}`,
                details: {
                    gasCostUSD: gasCostInUSD.toFixed(2),
                    grossProfitUSD: grossProfitUSD.toFixed(2),
                    amountOutUSD: amountOutInUSD.toFixed(2)
                }
            };
        }
    } catch (error) {
        console.error('Erreur technique lors du calcul de profitabilit√©:', error);
        finalReport.profitAnalysis.summary = '‚ùå Erreur technique interne lors du calcul de profit.';
    }
}

console.log('--- TRAITEMENT FINAL (V2) TERMIN√â ---');
return { json: finalReport };

