// On r√©cup√®re toutes les donn√©es d'entr√©e
const allInputs = $input.all();

// --- √âTAPE 1: EXTRACTION ET VALIDATION DES DONN√âES ---

// Identification robuste des diff√©rentes sources de donn√©es
const defiInputItem = allInputs.find(item => item.json.resultsArray !== undefined);
const currentDefiData = defiInputItem ? defiInputItem.json : null;

const priceInputItem = allInputs.find(item => item.json.priceData !== undefined);
const priceData = priceInputItem ? priceInputItem.json : null;

const historicalInputItem = allInputs.find(item => Array.isArray(item.json));
const historicalData = historicalInputItem ? historicalInputItem.json : [];

// R√©cup√©ration des param√®tres de la mission
const mission = $('Configuration Centrale').item.json;
const symbolsToWatch = mission.parametres_defi.symboles;

// Cas d'arr√™t si les donn√©es DeFi essentielles sont absentes
if (!currentDefiData || !currentDefiData.resultsArray || currentDefiData.resultsArray.length === 0) {
  return {
    json: {
      error: "Aucune donn√©e DeFi correspondante aux filtres trouv√©e aujourd'hui.",
      radarText: 'Aucune donn√©e √† analyser.',
      arbitrageReport: 'Aucune opportunit√© d\'arbitrage d√©tect√©e.',
      topOpportunity: { symbol: 'N/A', project: 'N/A', apy: 'N/A', opportunity_score: 'N/A', risk_level: 'N/A' },
      highestVolatility: { symbol: 'N/A', volatility: 'N/A' },
