    lowest: { project: 'N/A', apy: 'N/A' }
};

// --- √âTAPE 5: ASSEMBLAGE DE LA SORTIE FINALE ---

// Construction de l'objet JSON final avec des valeurs par d√©faut robustes
const finalJson = {
  alertePrincipale,
  trendReport,
  radarText,
  arbitrageReport,
  topOpportunity,
  highestVolatility,
  bestArbitrageOpportunity: bestArbitrageOpportunity,
