const mission = $('Configuration Centrale').item.json;
const params = mission.parametres_defi;

// Les param√®tres sont maintenant dynamiques, lus depuis la configuration.
const symbolsToWatch = params.symboles;
const protocolsToWatch = params.protocoles;
const minTvl = params.seuil_tvl;
const chainToWatch = params.chaine;

const allPools = $input.item.json.data;
const results = [];

// On boucle sur tous les pools re√ßus de l'API
for (const pool of allPools) {
  // On v√©rifie si le pool correspond √† nos crit√®res DYNAMIQUES
  if (
